customCss:
/* ========================= 
   TOPO DA NOTÍCIA (FIGMA)
   ========================= */

.blog-header-text { margin-bottom: 18px; }

/* Barrinha colorida curta */
.blog-color-strip { display: flex; overflow: hidden; }

.blog-color-strip--short{
  width: 92px; height: 6px; border-radius: 999px; margin-bottom: 10px;
}
.blog-color-strip--short span:nth-child(1){ flex:1; background:#E5258C; }
.blog-color-strip--short span:nth-child(2){ flex:1; background:#00B8F1; }
.blog-color-strip--short span:nth-child(3){ flex:1; background:#FFD400; }
.blog-color-strip--short span:nth-child(4){ flex:1; background:#F6F7FB; }

/* Linha divisória */
.blog-header-divider{
  margin-top: 14px; height: 1px; width: 100%; background: #D4D7E2;
}

/* =========================
   TEXTO INICIAL
   ========================= */

.blog-article{
  font-size: 15px; line-height: 1.65; color: #4B4B48;
}
.blog-article p{ margin: 0 0 14px; }

.blog-article .blog-lead{
  font-size: 16px; line-height: 1.55; font-weight: 600;
  color:#4B4B48; margin-bottom: 16px;
}
.blog-article strong{ font-weight: 800; }

/* Blindagem (evitar título duplicado do template) */
.blog-article > h1:first-child{ display:none; }
.blog-article > h2:first-child:not(.blog-tech-title){ display:none; }

/* =========================
   PONTOS-CHAVE DA TECNOLOGIA (FIGMA)
   ========================= */

.blog-tech-section{ margin: 26px 0 34px; }

.blog-tech-section .blog-tech-title{
  margin: 0 0 14px !important;
  font-size: 22px !important;
  line-height: 1.2 !important;
  font-weight: 800 !important;
  color: #5A5A5A !important;
}

.blog-tech-section .blog-tech-card{
  background: #D7DAE2 !important;
  border-radius: 30px !important;
  padding: 34px 40px !important;
  max-width: 780px !important;
  margin: 0 !important;
}

.blog-tech-section .blog-tech-list{
  margin: 0 !important;
  padding-left: 18px !important;
}

.blog-tech-section .blog-tech-list li{
  font-size: 15px !important;
  line-height: 1.6 !important;
  color: #5A5A5A !important;
  margin-bottom: 22px !important;
}
.blog-tech-section .blog-tech-list li:last-child{ margin-bottom: 0 !important; }

.blog-tech-section .blog-tech-list li::marker{
  font-size: 0.9em !important;
  color: #6B6F7B !important;
}

.blog-tech-section .blog-tech-list strong{
  font-weight: 800 !important;
  color: #3F3F3D !important;
}

/* =========================
   SUBSTRATOS + APLICAÇÕES (FIGMA)
   ========================= */

.blog-substrates-section{
  margin: 34px 0 40px;
  max-width: 780px;
}

.blog-substrates-section .blog-substrates-title,
.blog-substrates-section .blog-substrates-subtitle{
  color:#5A5A5A !important;
  font-weight: 800 !important;
}

.blog-substrates-title{
  margin: 0 0 18px !important;
  font-size: 22px !important;
  line-height: 1.25 !important;
}

.blog-substrates-subtitle{
  margin: 26px 0 14px !important;
  font-size: 18px !important;
  line-height: 1.25 !important;
}

/* lista centralizada com bullet no lado */
.blog-substrates-list{
  display: inline-block;
  margin: 12px auto 30px;
  padding-left: 22px;
  list-style: disc;
  list-style-position: outside;
  text-align: left;
  max-width: 430px;
  color:#6B6F7B;
  font-style: italic;
  font-weight: 500;

  position: relative;
  left: 50%;
  transform: translateX(-50%);
}

.blog-substrates-list li{
  margin: 10px 0;
  font-size: 15px;
  line-height: 1.55;
}

/* remove sublinhado herdado */
.blog-substrates-list a,
.blog-substrates-list span,
.blog-substrates-list li{
  text-decoration: none !important;
}

.blog-applications-list{
  margin: 0 0 18px;
  padding-left: 22px;
  color:#4B4B48;
}

.blog-applications-list li{
  font-size: 15px;
  line-height: 1.6;
  margin-bottom: 12px;
}

.blog-substrates-highlight{
  font-size: 15px;
  line-height: 1.65;
  color:#4B4B48;
  margin-top: 12px;
  max-width: 720px;
}

.blog-substrates-highlight strong{ font-weight: 800; }

/* =========================
   OFFSET UV + DIGITAL (padrão cinza)
   ========================= */

.blog-offset-section{
  margin: 18px 0 36px;
  max-width: 780px;
}

.blog-offset-title{
  margin: 0 0 10px;
  font-size: 16px;
  line-height: 1.35;
  font-weight: 800;
  color:#5A5A5A;
}

.blog-offset-title span{
  font-weight: 600;
  color:#5A5A5A;
}

.blog-offset-subtitle{
  margin: 14px 0 10px;
  font-size: 18px;
  font-weight: 800;
  color:#5A5A5A;
}

.blog-offset-text,
.blog-offset-highlight{
  font-size: 15px;
  line-height: 1.65;
  color:#4B4B48;
  margin: 0 0 12px;
}

.blog-offset-list{
  margin: 0 0 12px;
  padding-left: 18px;
}
.blog-offset-list li{ margin: 0 0 10px; color:#4B4B48; }

/* =========================================================
   TIMELINE (FIGMA) — LINHA NO MEIO + ALTERNÂNCIA
   Estrutura usada: .timeline-wrapper / .timeline-event
   ========================================================= */

.blog-timeline{ 
  margin: 64px auto 40px;
  max-width: 980px;
}

.blog-timeline *{ box-sizing: border-box; }

/* pill */
.blog-timeline .timeline-pill{
  display: inline-block;
  background: #E5258C;
  color: #fff;
  font-weight: 800;
  font-size: 16px;
  padding: 14px 26px;
  border-radius: 999px;
  margin-bottom: 56px;
}

/* wrapper */
.blog-timeline .timeline-wrapper{
  position: relative;
  display: flex;
  justify-content: space-between;
  gap: 48px;
  padding: 72px 0 72px;     /* dá espaço pro conteúdo acima/abaixo */
  min-height: 280px;        /* garante “palco” */
}

/* linha central (no meio) */
.blog-timeline .timeline-line{
  position: absolute;
  left: 0;
  right: 0;
  top: 50%;
  height: 8px;
  border-radius: 999px;
  background: rgba(229, 37, 140, 0.35);
  transform: translateY(-50%);
  z-index: 1;
}

/* segmento rosa forte no final (direita) */
.blog-timeline .timeline-line::after{
  content: "";
  position: absolute;
  right: 0;
  top: 0;
  height: 8px;
  width: 34%;
  border-radius: 999px;
  background: #E5258C;
}

/* evento */
.blog-timeline .timeline-event{
  position: relative;
  width: 32%;
  z-index: 2;
}

/* marcador (vertical) — nasce na linha */
.blog-timeline .timeline-marker{
  position: absolute;
  left: 50%;
  width: 2px;
  height: 56px;
  background: #8A8E9B;
  transform: translateX(-50%);
}

/* marcador descendo da linha */
.blog-timeline .timeline-marker--down{
  top: 50%;
  transform: translate(-50%, 0);
}

/* marcador subindo até a linha */
.blog-timeline .timeline-marker--up{
  top: 50%;
  transform: translate(-50%, -100%);
}

/* cabeça (ano/mês) acima */
.blog-timeline .timeline-head--above{
  position: absolute;
  left: 0;
  top: calc(50% - 118px);
  text-align: left;
}

/* ano e mês */
.blog-timeline .timeline-year{
  display: block;
  color: #E5258C;
  font-weight: 900;
  font-size: 28px;
  line-height: 1;
}

.blog-timeline .timeline-month{
  display: block;
  color: #5A5A5A;
  font-weight: 900;
  font-size: 30px;
  line-height: 1;
  margin-top: 2px;
}

/* texto abaixo da linha (Nov/Jan) */
.blog-timeline .timeline-text--below{
  position: absolute;
  left: 0;
  top: calc(50% + 62px);
  max-width: 280px;
  font-size: 14px;
  line-height: 1.6;
  color: #4B4B48;
  text-align: left;
  margin: 0;
}

/* texto acima da linha (Dezembro) */
.blog-timeline .timeline-text--above{
  position: absolute;
  left: 0;
  top: calc(50% - 128px);
  max-width: 320px;
  font-size: 14px;
  line-height: 1.6;
  color: #4B4B48;
  text-align: left;
  margin: 0;
}

/* mês abaixo da linha (Dezembro) */
.blog-timeline .timeline-month--below{
  position: absolute;
  left: 0;
  top: calc(50% + 62px);
  color: #5A5A5A;
  font-weight: 900;
  font-size: 30px;
  line-height: 1;
}

/* destaques rosas no texto */
.blog-timeline .timeline-text strong{
  color: #E5258C;
  font-weight: 900;
}

/* Ajuste de alinhamento por coluna */
.blog-timeline .timeline-event--left  { text-align: left; }
.blog-timeline .timeline-event--mid   { text-align: left; }
.blog-timeline .timeline-event--right { text-align: left; }

/* =========================================================
   RESPONSIVO
   ========================================================= */
@media (max-width: 900px){

  .blog-tech-section .blog-tech-title{
    font-size: 18px !important;
    margin-bottom: 12px !important;
  }

  .blog-tech-section .blog-tech-card{
    max-width: 100% !important;
    padding: 22px 20px !important;
    border-radius: 22px !important;
  }

  .blog-substrates-list{ max-width: 320px; }

  /* Timeline mobile: empilha e remove linha */
  .blog-timeline .timeline-wrapper{
    display: block;
    padding: 20px 0;
    min-height: auto;
  }

  .blog-timeline .timeline-line{ display:none; }
  .blog-timeline .timeline-marker{ display:none; }

  .blog-timeline .timeline-event{
    width: 100%;
    padding: 12px 0 18px;
  }

  .blog-timeline .timeline-head--above,
  .blog-timeline .timeline-text--below,
  .blog-timeline .timeline-text--above,
  .blog-timeline .timeline-month--below{
    position: static;
    max-width: 100%;
  }

  .blog-timeline .timeline-text--below,
  .blog-timeline .timeline-month--below{
    margin-top: 10px;
  }
}

/* =========================
   FINAL DA NOTÍCIA (FIGMA)
   ========================= */

.blog-final{
  margin: 36px 0 26px;
  max-width: 920px;
}

.blog-final-lead,
.blog-final-outro{
  font-size: 15px;
  line-height: 1.65;
  color: #4B4B48;
  margin: 0 0 18px;
}

.blog-final-lead strong,
.blog-final-outro strong{
  font-weight: 800;
}

/* Card */
.blog-final-card{
  background: #E7E9F0;          /* cinza claro tipo Figma */
  border: 2px solid #D4D7E2;    /* borda suave */
  border-radius: 22px;
  padding: 22px 26px;
  margin: 16px 0 18px;
}

.blog-final-card-title{
  margin: 0 0 14px;
  font-size: 20px;
  line-height: 1.2;
  font-weight: 900;
  color: #5A5A5A;
}

/* Lista com hífen (–) */
.blog-final-list{
  list-style: none;
  margin: 0;
  padding: 0;
  display: grid;
  gap: 12px;
}

.blog-final-list li{
  position: relative;
  padding-left: 18px;
  font-size: 15px;
  line-height: 1.55;
  color: #4B4B48;
}

.blog-final-list li::before{
  content: "–";
  position: absolute;
  left: 0;
  top: 0;
  color: #5A5A5A;
  font-weight: 900;
}

/* Negritos do card */
.blog-final-list strong{
  font-weight: 900;
  color: #3F3F3D;
}

/* Última linha rosa */
.blog-final-list li.is-pink{
  color: #E5258C;
}

.blog-final-list li.is-pink::before{
  color: #E5258C;
}

@media (max-width: 900px){
  .blog-final{
    max-width: 100%;
  }

  .blog-final-card{
    padding: 18px 18px;
    border-radius: 18px;
  }

  .blog-final-card-title{
    font-size: 18px;
  }

  .blog-final-list li,
  .blog-final-lead,
  .blog-final-outro{
    font-size: 14.5px;
  }
}
